  <br>
  <br>
  <form class="login" @submit.prevent="onsubmit">
    <va-input
      v-model="email"
      class="mb-4"
      type="email"
      :label="t('auth.email')"
      :error="!!emailErrors.length"
      :error-messages="emailErrors"
    />

    <br>
    <div 
       id="volver" class="auth-layout__options flex items-center justify-between">
      <router-link class="ml-1 va-link" :to="{ name: 'login' }">
        volver a la pagina anterior
      </router-link>
    </div>

    <div class="flex justify-center mt-4">
      <va-button @click="onsubmit" class="my-0">{{ t('Recuperar contraseña') }}</va-button>
    </div>
  </form>
