</template>

<script setup lang="ts">
  import { computed, ref } from 'vue'
  import { useRouter } from 'vue-router'
  import { useI18n } from 'vue-i18n'
  import { isEmail } from '../../../services/utils';
  import { useAuthStore } from '../../../stores/auth-store'
  import { Store } from 'pinia';

  const store = useAuthStore();
  const { t } = useI18n()
  const email = ref('')
  const emailErrors = ref<string[]>([])
  const formReady = computed(() => !emailErrors.value.length)
  const router = useRouter()

  function onsubmit() {
    if (!formReady.value) return
    emailErrors.value = validateEmail(email.value);

    recoverPassword(email.value);
  }

  function validateEmail(email:string){
    const errorList = [];
    if (!email) {
      emailErrors.value = ['El email es obligatorio']
    } 
    if (email && !isEmail(email) ) {
      errorList.push('El email ingresado no tiene el formato correcto');
    }

    return errorList
    }

  function recoverPassword(email:string){
    store.recoverPass(email,onEmailSubmit);
  }
  function onEmailSubmit(email:string){
   router.push('auth/login')
  }
</script>
